var Combat = Enumerable.Range(8, 11).Append(0).Append(36);
var gearSets = GearsetManager.GearSets.Where(i => i.InUse && !Combat.Contains((int)i.Class)).ToList().OrderByDescending(i=> i.Gear.First().Item.ItemLevel);
if (gearSets.Any())
{
  gearSets.First().Activate();
  await Coroutine.Sleep(2000);
}


uint[] questNpcIds = {2012880, 2012881, 2012882, 2012883};
var locationList = new List<Vector3>()
{
		new Vector3("-669.4896, -36.42328, 522.6367"),
		new Vector3("-746.0593, -38.92578, 597.0397"),
		new Vector3("-652.4605, -31.78455, 554.467"),
		new Vector3("-729.9458, -38.86481, 600.5187")
};
GameObject npcId = default;

foreach (var location in locationList)
{
		await LlamaLibrary.Helpers.Navigation.FlightorMove(location);
		npcId =  GameObjectManager.GameObjects.Where(r => r.IsTargetable && questNpcIds.Contains(r.NpcId))
				.OrderBy(r => r.Distance()).FirstOrDefault();

		if (npcId != default)
		{
				break;
		}
}

if (npcId == default)
{
		//Log something here
		return;
}
if (!npcId.IsWithinInteractRange)
{
		var _target = npcId.Location;
		await LlamaLibrary.Helpers.Navigation.FlightorMove(_target,4);
}
npcId.Interact();
await Coroutine.Sleep(7000);



